package examlpe;

public class hapchaggop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int first = 58;
		int second = 72;
		
		
		
		System.out.println("첫 번째 정수를 입력하세요 :" + first );
		System.out.println("두 번째 정수를 입력하세요 :" + second );
		System.out.println("합:"+(first+second));
		System.out.println("차:"+(first-second));
		System.out.println("곱:"+(first*second));
				

	}

}
